INSERT INTO fw_achievement (name, description, needed, category) VALUES ("CHOTA Crusher", "Fierce warriors with mutant abilities, the Children of the Apocalypse are primarily anarchists who spurn technology and organized society, but you\'ve managed to tame these savages in 15 separate fights.", 15, 1);
INSERT INTO fw_achievement_count (achievementid, itemid, itemtype) VALUES (1, 1, 4);
INSERT INTO fw_achievement (name, description, needed, category) VALUES ("Enforcer Ender", "Enforcers think of themselves as the world\'s policemen; comprised of former military officers, local law enforcement, and even park rangers, these G. I. Joes think they can marshal law their way through the apocalypse. However, you kicked butt in 15 fights against the Enforcers, proving you won\'t bow down to The Man.", 15, 1);
INSERT INTO fw_achievement_count (achievementid, itemid, itemtype) VALUES (2, 2, 4);
INSERT INTO fw_achievement (name, description, needed, category) VALUES ("Lightbearer Liquidator", "Lightbearers are all about organic medicines and Kung Fu philosophies, which have no place in the harsh reality of the apocalypse. Once you learned to sweep their legs, you managed to master 15 altercations against Lightbearers.", 15, 1);
INSERT INTO fw_achievement_count (achievementid, itemid, itemtype) VALUES (3, 3, 4);
INSERT INTO fw_achievement (name, description, needed, category) VALUES ("Tech Trasher", "Techs are cowards who like to hide behind Enforcers while playing gods in their labs. They pollute the earth and show no self-restraint in the pursuit of Science. Luckily, you took Revenge on the Nerds and owned 15 Techs.", 15, 1);
INSERT INTO fw_achievement_count (achievementid, itemid, itemtype) VALUES (4, 4, 4);
INSERT INTO fw_achievement (name, description, needed, category) VALUES ("Traveler Thumper", "Much like the mafia groups from the World Before, Travelers are a loose affiliation of \'families\' that have their fingers poked into a variety of pies: mostly smuggling, narcotics, and even assassinations. But what have you ever done to deserve such disrespect? These Goodfellas will think twice now that you\'re the victorious badass in 15 fights against their kind.", 15, 1);
INSERT INTO fw_achievement_count (achievementid, itemid, itemtype) VALUES (5, 5, 4);
INSERT INTO fw_achievement (name, description, needed, category) VALUES ("Vista Vanquisher", "Vistas like to think of themselves as guerrilla warriors, farmers, ranchers, and craftsmen, but everyone knows they are just crazy homeless hippies. You showed those stoned-out tree-huggers what-for by winning 15 fights against the Vista.", 15, 1);
INSERT INTO fw_achievement_count (achievementid, itemid, itemtype) VALUES (6, 6, 4);
INSERT INTO fw_achievement (name, description, needed, category, achievement_type) VALUES ("Vendor", "There are plenty of goods you can scavenge or items you can loot out there in the vast wasteland. Now that you\'ve sold 100 salvaged items, you have the honor of calling yourself a Vendor--which is sort of a freelance Merchant. Pretty soon, folks will think of you as a walking one-stop-shop store! ", 100, 2, 1);
