INSERT INTO fw_achievement (name, description, needed, category) VALUES ("Raking it in", "With more than 1,000,000 chips, you now have almost as much money as God. Food, medicine, ammo, equipment--pretty much any supplies you can buy--the post-apocalyptic world is your oyster.", 1000000, 2);
